+import AdminMenu from "./AdminMenu";
+import OrderList from "./OrderList";
+import Loader from "../../components/Loader";
+
+const DashboardCard = ({ label, icon, value, loading }) => (
+  <div className="rounded-lg bg-neutral-900 p-5 w-[20rem] mt-5 shadow">
+    <div className="font-bold rounded-full w-[3rem] h-[3rem] bg-pink-500 flex items-center justify-center text-white text-lg">
+      {icon}
+    </div>
+    <p className="mt-5 text-white">{label}</p>
+    <h1 className="text-xl font-bold text-white mt-1">
+      {loading ? <Loader /> : value}
+    </h1>
+  </div>
+);
+
+const AdminDashboard = () => {
+  const { data: sales, isLoading: loadingSales } = useGetTotalSalesQuery();
+  const { data: customers, isLoading: loadingCustomers } = useGetUsersQuery();
+  const { data: orders, isLoading: loadingOrders } = useGetTotalOrdersQuery();
+  const { data: salesDetail } = useGetTotalSalesByDateQuery();
+
+  const [chartState, setChartState] = useState({
+    options: {
+      chart: { type: "line" },
+      tooltip: { theme: "dark" },
+      colors: ["#00E396"],
+      dataLabels: { enabled: true },
+      stroke: { curve: "smooth" },
+      title: {
+        text: "Sales Trend",
+        align: "left",
+        style: { color: "#fff" },
+      },
+      grid: { borderColor: "#ccc" },
+      markers: { size: 1 },
+      xaxis: {
+        categories: [],
+        title: { text: "Date", style: { color: "#fff" } },
+        labels: { style: { colors: "#fff" } },
+      },
+      yaxis: {
+        title: { text: "Sales", style: { color: "#fff" } },
+        labels: { style: { colors: "#fff" } },
+        min: 0,
+      },
+      legend: {
+        labels: { colors: "#fff" },
+        position: "top",
+        horizontalAlign: "right",
+        floating: true,
+        offsetY: -25,
+        offsetX: -5,
+      },
+    },
+    series: [{ name: "Sales", data: [] }],
+  });
+
+  useEffect(() => {
+    if (salesDetail) {
+      const formatted = salesDetail.map((item) => ({
+        x: item._id,
+        y: item.totalSales,
+      }));
+
+      setChartState((prev) => ({
+        ...prev,
+        options: {
+          ...prev.options,
+          xaxis: {
+            ...prev.options.xaxis,
+            categories: formatted.map((item) => item.x),
+          },
+        },
+        series: [
+          {
+            name: "Sales",
+            data: formatted.map((item) => item.y),
+          },
+        ],
+      }));
+    }
+  }, [salesDetail]);
+
+  return (
+    <>
+      <AdminMenu />
+
+      <section className="xl:ml-[4rem] md:ml-[0rem]">
+        <div className="w-[80%] flex justify-around flex-wrap">
+          <DashboardCard
+            label="Sales"
+            icon="â‚¹"
+            value={`â‚¹ ${sales?.totalSales?.toFixed(2) ?? "0.00"}`}
+            loading={loadingSales}
+          />
+          <DashboardCard
+            label="Customers"
+            icon="ðŸ‘¥"
+            value={customers?.length ?? 0}
+            loading={loadingCustomers}
+          />
+          <DashboardCard
+            label="All Orders"
+            icon="ðŸ“¦"
+            value={orders?.totalOrders ?? 0}
+            loading={loadingOrders}
+          />
+        </div>
+
+        <div className="ml-[10rem] mt-[4rem]">
+          <Chart
+            options={chartState.options}
+            series={chartState.series}
+            type="bar"
+            width="70%"
+          />
+        </div>
+
+        <div className="mt-[4rem]">
+          <OrderList />
+        </div>
+      </section>
+    </>
+  );
+};
+
+export default AdminDashboard;

